#!/usr/bin/env node
export type Role = 'user' | 'assistant' | 'system';
export type Action = 'next' | 'variant';
export interface APIMessage {
    role: Role;
    content: string;
}
export interface APIRequest {
    model: string;
    action: Action;
    messages: APIMessage[];
}
export interface APIResponse {
    whisper?: string;
    choices: {
        delta?: APIMessage;
        message: APIMessage;
    }[];
}
